create table cadastrar_cnpj (data_abertura_empresa date, id_cadastrar_cnpj number(10,0) generated as identity, qtd_funcionarios number(10,0), cnpj varchar2(255 char), inscricao_estadual varchar2(255 char), porte_da_empresa varchar2(255 char), razao_social varchar2(255 char), primary key (id_cadastrar_cnpj));
create table cadastro (data_nascimento date, renda float(53), sexo varchar2(1 char), id_cadastrar number(19,0) generated as identity, escolaridade varchar2(255 char), estado_civil varchar2(255 char), nome varchar2(255 char), profissao varchar2(255 char), primary key (id_cadastrar));
create table contato (id_contato number(10,0) generated as identity, email varchar2(255 char), telefone_celular varchar2(255 char), telefone_fixo varchar2(255 char), primary key (id_contato));
create table documentos (id_documentos number(10,0) generated as identity, cpf varchar2(255 char), rg varchar2(255 char), primary key (id_documentos));
create table endereco (numero number(10,0), id_endereco number(19,0) generated as identity, bairro varchar2(255 char), cep varchar2(255 char), cidade varchar2(255 char), complemento varchar2(255 char), estado varchar2(255 char), logradouro varchar2(255 char), primary key (id_endereco));
